import './App.css';
import About from './compponents/About';
import Navbar from './compponents/Navbar';
import TextForm from './compponents/TextForm';
import React, { useState } from 'react';
import Alert from './compponents/Alert';

function App() {
  const [mode,setMode]=useState('light');
  const[alert , setAlert]=useState(null);
  const togglered=()=>
  {
    if(mode==='light')
    {
      setMode('red');
    }
    else{
      setMode('white')
    }
  }
  const showAlert=(message, type)=>
  {
    // alert as an object
    setAlert(
      {
        msg: message,
        type:type
      }
    )
    setTimeout(()=>
    {
setAlert(null);
    },1500);
  }
  const toggleMode=()=>
  {
    if(mode==='light')
    {

      setMode('dark');
      document.body.style.backgroundColor='rgb(38 38 39)';
      showAlert("dark mode has been enabled", "success");
    }

    else
    {
      setMode('light');
      document.body.style.backgroundColor='white';
      showAlert("light mode has been enabled" , "success");
    }

  }

  //state variable whether dark mode is enabled or not*/
  return (
<>
 <Navbar title="Text-Editor" mode={mode}  toggleMode={toggleMode}/>
 <Alert alert={alert}/>
<div className="container">
<TextForm showAlert={showAlert} heading= "Enter text to Analyze below" mode={mode} />
</div>
<div className="container">
<About mode={mode}/>
</div>
 </>
  );


}

export default App;

