import './App.css';
import About from './compponents/About';
import Navbar from './compponents/Navbar';
import TextForm from './compponents/TextForm';
import React, { useState } from 'react';

function App() {
  const [mode,setMode]=useState('light');
  const toggleMode=()=>
  {
    if(mode==='light')
    {

      setMode(
      {
       color:white ,
       backgroundColor:black
      });
    }
    else
    {
      setMode('light')
    }

  }
  //state variable whether dark mode is enabled or not
  return (
    <>
<Navbar title="Text-Editor" mode={mode} toggleMode={toggleMode}/>
<div className="container">
<TextForm heading= "Enter text to Analyze below" />
</div>
<div className="container">
<About/>
</div>
 </>

  );
}

export default App;

