import React ,{useState} from 'react' 
export default function TextForm(props) 
{
    const handleUpClick=()=>
    {
     console.log("uppercase is clicked" + {text} ) ;
     let newText=text.toUpperCase();
     setText(newText)
    }
    const handleOnChange=(event)=>
    {
     console.log("onchange")
     setText(event.target.value)
    }
    const [text,setText]=useState('enter your text');
    //text STATE variable conatain enter text value fun is setText to update

  return (
    <>
  <div>
  <h2>{props.heading} </h2>
  <div className="mb-3">
  <textarea className="form-control"  value={text} onChange={handleOnChange} id="mb1" rows="8"></textarea>
  </div>
  <button className="btn btn-primary" onClick={handleUpClick}>Convert to upercase</button>
  <div className="conatiner my-3">
    <h3>{text.split( " ")} , {text.length} characters</h3>
  </div>
  </div>
  </>
  )
}
//situation of component content as avariable after performing actions updated  the state
